mapper.select:
SELECT count(*) FROM admin
WHERE admin_id=#{adminId}
AND admin_pw=#{adminPw}

SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			member_no, id, nick
		FROM member
		WHERE 1=1


				AND member_no = #{search}


				AND ${searchOpt} LIKE '%' || #{search} || '%' 


		ORDER BY member_no DESC
	) B
) BOARD
WHERE rnum BETWEEN #{startNo } AND #{endNo }

SELECT count(*) FROM member WHERE 1=1
AND member_no = #{search}
AND ${searchOpt} LIKE '%' || #{search} || '%'

SELECT member_no, id, name, nick, email, phone, addr1, addr2, addr3, mdate
FROM member
WHERE member_no=${memberNo}

mapper.update:
UPDATE member 



	, id=#{id}


	, name=#{name}


	, nick=#{nick}


	, email=#{email}


	, phone=#{phone}


	, addr1=#{addr1}


	, addr2=#{addr2}


	, addr3=#{addr3}




WHERE member_no = #{memberNo}

mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			boardNo, btitle, id, hit
		FROM board
		WHERE 1=1	

				 AND cateno=${board.cateno}



				AND ${paging.searchOpt} LIKE '%' || #{paging.search} || '%' 


		ORDER BY boardno DESC
	) B
) BOARD
WHERE rnum BETWEEN #{paging.startNo } AND #{paging.endNo }

SELECT count(*) FROM board WHERE 1=1
AND cateno=${board.cateno}
AND ${paramData.searchOpt} LIKE '%' || #{paramData.search} || '%'

SELECT boardNo, btitle, bcontent, id, nick, hit, bdate, cateno
FROM board
WHERE boardno = #{boardNo}

SELECT count(*)
FROM boardfile
WHERE boardno = #{boardNo}

SELECT fileno, boardno, origin_name, stored_name
FROM boardfile
WHERE boardno = #{boardNo}

SELECT count(*)
FROM replytb
WHERE boardno =#{boardNo}

SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT replyno, boardno, id, content, bdate
		FROM replytb
		WHERE boardno = #{board.boardNo}
		ORDER BY bdate DESC
	) B
) BOARD
WHERE rnum BETWEEN #{paging.startNo } AND #{paging.endNo }
